<?php

namespace App\Http\Controllers\utility;

use App\Http\Controllers\Controller;
use Illuminate\Http\Request;

/**
 * utility/createReferenceID/{identifier}
 * \App\Http\Controllers\utility\ReferenceID::create($identifier);
 */

class ReferenceID extends Controller
{
    public static function create($identifier) {

        $DATE   = date('m').date('d').date('Y').date('h').date('i').date('s');
        $CHAR   = str_shuffle("");

        return strtoupper($identifier) ."-".$DATE."-".substr($CHAR, 0, 3);
    }
}
